import React from 'react'
import {useForm} from 'react-hook-form';

function Addtodo(props) {
    let{register,
        handleSubmit,
        formState:{errors },
    }=useForm();

formsubmit=(todoObj)=>{
    props.addnewtodo(todoObj.newTodo)
}

  return (
    <div>
        <h3>Addtodo</h3>
        <form onSubmit={handleSubmit()}>
                                 
                    <input  type="text"  id="name" className="form-control mb-3" 
                    placeholder="activity"
                    {...register("newTodo" ,{required:true})}/> 

                  <button type="submit" className="btn btn-success ms-3 mt-3">
                   submit</button>
              </form>
    </div>
  )
}

export default Addtodo
